import React from 'react';
import { LineChart, Line, CartesianGrid, XAxis, YAxis, Tooltip, Legend } from 'recharts';

const Chart = ({ data, metric }) => {
  // Extract months from the first row of data
  const months = data[0].slice(1);

  // Find the index of the selected metric
  const metricIndex = data.findIndex(row => row[0] === metric);

  // Extract values for the selected metric
  const metricValues = data[metricIndex].slice(1);

  // Combine months with corresponding metric values
  const chartData = months.map((month, index) => ({
    month,
    [metric]: metricValues[index]
  }));

  return (
    <LineChart width={800} height={400} data={chartData} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
      <CartesianGrid strokeDasharray="3 3" />
      <XAxis dataKey="month" />
      <YAxis />
      <Tooltip />
      <Legend />
      <Line type="monotone" dataKey={metric} stroke="#8884d8" />
    </LineChart>
  );
};

export default Chart;
