import React, { useState, useEffect } from 'react';
import Chart from './components/Chart';

function App() {
  const [data, setData] = useState([]);
  const [metrics, setMetrics] = useState([]);
  const [selectedMetric, setSelectedMetric] = useState('');

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/data');
      const jsonData = await response.json();
      console.log('Data:', jsonData); // Log the fetched data
      setData(jsonData);
      setMetrics(jsonData.slice(1).map(row => row[0])); // Extracting metric names from the first column
      setSelectedMetric(jsonData[0][1]); // Assuming the first metric is selected initially
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleMetricChange = (event) => {
    setSelectedMetric(event.target.value);
  };

  return (
    <div className="App" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100vh', color: '#fff' }}>
      <header className="App-header">
        <h1>Vera's Environment Dashboard</h1>
        <p>Welcome to Vera's Environmental Metrics Dashboard. Below, you can find data for various environmental metrics for the year 2023. This dashboard visualizes data for water consumption, natural gas consumption, grid electricity consumption, steam consumption, food waste, solar energy production, and water recycled.</p>
      </header>
      <div className="container">
        <select className="dropdown" value={selectedMetric} onChange={handleMetricChange}>
          {metrics.map((metric, index) => (
            <option key={index} value={metric}>{metric}</option>
          ))}
        </select>
        <Chart data={data.slice(1)} metric={selectedMetric} />
      </div>
    </div>
  );
}

export default App;
