import React from 'react';
import { LineChart, Line, CartesianGrid, XAxis, YAxis, Tooltip, Legend } from 'recharts';

const Chart = ({ data, metric }) => {
  console.log('Chart Data:', data); // Log the received data
  console.log('Selected Metric:', metric); // Log the selected metric

  return (
    <LineChart width={800} height={400} data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
      <CartesianGrid strokeDasharray="3 3" />
      <XAxis dataKey="month" />
      <YAxis />
      <Tooltip />
      <Legend />
      <Line type="monotone" dataKey={metric} stroke="#8884d8" />
    </LineChart>
  );
};

export default Chart;
