import React, { useState, useEffect } from 'react';
import Chart from './components/Chart';

function App() {
  const [data, setData] = useState([]);
  const [selectedMetric, setSelectedMetric] = useState('');

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/data');
      const jsonData = await response.json();
      setData(jsonData);
      setSelectedMetric(jsonData[0][1]); // Assuming the first metric is selected initially
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleMetricChange = (event) => {
    setSelectedMetric(event.target.value);
  };

  return (
    <div className="App">
      <h1>Environmental Metrics Dashboard</h1>
      <select value={selectedMetric} onChange={handleMetricChange}>
        {data[0] && data[0].map((metric, index) => (
          <option key={index} value={metric}>{metric}</option>
        ))}
      </select>
      <Chart data={data.slice(1)} metric={selectedMetric} />
    </div>
  );
}

export default App;
