import React from 'react';
import { LineChart, Line, CartesianGrid, XAxis, YAxis, Tooltip, Legend } from 'recharts';

const CustomTooltip = ({ active, payload, label }) => {
  if (active && payload && payload.length) {
    return (
      <div className="custom-tooltip" style={{ backgroundColor: '#f5f5f5', padding: '10px', border: '1px solid #ccc' }}>
        <p className="label" style={{ color: 'green' }}>{`${label} : ${payload[0].value}`}</p>
      </div>
    );
  }

  return null;
};

const Chart = ({ data, metric }) => {
  if (!data || data.length === 0) {
    return null;
  }

  const months = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];
  const metricsData = data.slice(1);
  const metricIndex = metricsData.findIndex(row => row[0] === metric);

  if (metricIndex === -1) {
    return null;
  }

  const metricValues = metricsData[metricIndex].slice(1);
  const chartData = months.map((month, index) => ({
    month,
    [metric]: metricValues[index]
  }));

  return (
    <div className="container">
      <LineChart width={1100} height={400} data={chartData} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
        <CartesianGrid strokeDasharray="3 3" stroke="#ccc" />
        <XAxis 
          dataKey="month" 
          label={{ 
            value: 'Months (2023)', 
            position: 'insideBottom', 
            offset: -10,
            style: { fill: 'white' }
          }} 
          stroke="#000" 
          tick={{ fill: '#000', fontWeight: 'bold' }} 
        />
        <YAxis 
          stroke="#000" 
          tick={{ fill: '#000', fontWeight: 'bold' }} 
          label={{ 
            value: metric, 
            angle: -90, 
            position: 'insideLeft',
            style: { textAnchor: 'middle' }
          }}
        />
        <Tooltip content={<CustomTooltip />} />
        <Legend />
        <Line type="monotone" dataKey={metric} stroke="#fff" activeDot={{ r: 8 }} />
      </LineChart>
    </div>
  );
};

export default Chart;
