import React from 'react';
import { ResponsiveContainer, LineChart, XAxis, YAxis, CartesianGrid, Tooltip, Legend, Line } from 'recharts';


const Chart = ({ data, metric }) => {
  return (
    <ResponsiveContainer width="100%" height={400}>
      <LineChart data={data} margin={{ top: 20, right: 30, left: 20, bottom: 50 }}>
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis 
          dataKey="month" 
          label={{ 
            value: 'Months (2023)', 
            position: 'insideBottom', 
            offset: -20,
            dy: 20,
            dx: -20,
            style: { fill: 'white' }
          }} 
          stroke="#000" 
          tick={{ fill: '#000', fontWeight: 'bold', textAnchor: 'middle' }} 
        />
        <YAxis 
          stroke="#000" 
          tick={{ fill: '#000', fontWeight: 'bold' }} 
          label={{ 
            value: metric, 
            angle: -90, 
            position: 'insideLeft',
            style: { textAnchor: 'middle', fill:'white' }
          }}
        />
        <Tooltip content={<CustomTooltip />} />
        <Legend />
        <Line type="monotone" dataKey={metric} stroke={'#fff'} strokeWidth={3} activeDot={{ r: 8 }} />
      </LineChart>
    </ResponsiveContainer>
  );
};

export default Chart;
